import React, { useEffect, useRef, useState } from 'react';
import { FaBars } from 'react-icons/fa';
import { useGlobalContext } from './context';

const Home = () => {

    let [counter,setCounter] = useState(0)
     let RenderRef = useRef(0)
     let BtnRef = useRef()
     let ParaRef = useRef()

    useEffect(()=>{

        RenderRef.current+=1;
        console.log(`Rendered ${RenderRef.current} Times`);
      BtnRef.current.style.backgroundColor='red'
    })

    let Click =(  ()=>{
      setCounter(counter+1)
      ParaRef.current.style.fontSize='24px'

    } )


  const { openSidebar, openModal } = useGlobalContext();
  return (
    <main>
      <button onClick={openSidebar} className='sidebar-toggle'>
        <FaBars />
      </button>
      <button ref={ParaRef} onClick={openModal} className='btn'>
        show modal
      </button>
      <button ref={BtnRef} onClick={Click} className='btn'>
        Click Me
      </button>
    </main>
  );
};

export default Home;
